import React, { useState } from 'react';
import { Modal, Image, Button, Rating } from 'semantic-ui-react';
import { useCart } from './CartContext';

const ProductModal = ({ product, onClose }) => {
  const [isBought, setIsBought] = useState(false);
  const { addToCart } = useCart();

  const handleAddToCartClick = () => {
    addToCart(product);
    setIsBought(true);
  };

  return (
    <Modal open onClose={onClose}>
      <Modal.Header>{product.title}</Modal.Header>
      <Modal.Content image>
        <Image size="medium" src={product.image} alt={product.title} />
        <Modal.Description>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
          <p>Category: {product.category}</p>
          <p>Rating: {product.rating.rate} ({product.rating.count} reviews)</p>
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        {isBought ? (
          <p>Congratulations! You have bought {product.title}</p>
        ) : (
          <>
            <Button primary onClick={handleAddToCartClick}>
              Add to Cart
            </Button>
            <Button onClick={onClose}>Close</Button>
          </>
        )}
      </Modal.Actions>
    </Modal>
  );
};

export default ProductModal;
